input = open("day9_input.txt")
i = input.readline()[:-1]

grid = [ ["................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................"] for i in range(5000)]

head_x = 500
head_y = 500
tail_x = 500
tail_y = 500
	
grid[tail_y][0] = grid[tail_y][0][:tail_x] + "#" + grid[tail_y][0][tail_x + 1:]

def CheckIfSkip(head_y, head_x, tail_y, tail_x):
	if(abs(head_y - tail_y) <= 1 and abs(head_x - tail_x) <= 1):
		return True
	return False

while i:
	instruction = i.split(" ")
	direction = instruction[0]
	amount = instruction[1]

	match direction:
		case "U":
			for _ in range(int(amount)):
				head_y -= 1

				if(CheckIfSkip(head_y, head_x, tail_y, tail_x)):
					continue

				if(head_y - tail_y < -1):
					tail_y -= 1
					if(head_x < tail_x):
						tail_x -= 1
					elif(head_x > tail_x):
						tail_x += 1
				grid[tail_y][0] = grid[tail_y][0][:tail_x] + "#" + grid[tail_y][0][tail_x + 1:]

		case "D":
			for _ in range(int(amount)):
				head_y += 1
				if(CheckIfSkip(head_y, head_x, tail_y, tail_x)):
					continue

				if(head_y - tail_y > 1):
					tail_y += 1
					if(head_x < tail_x):
						tail_x -= 1
					elif(head_x > tail_x):
						tail_x += 1
				grid[tail_y][0] = grid[tail_y][0][:tail_x] + "#" + grid[tail_y][0][tail_x + 1:]

		case "L":
			for _ in range(int(amount)):
				head_x -= 1
				if(CheckIfSkip(head_y, head_x, tail_y, tail_x)):
					continue

				if(head_x - tail_x < -1):
					tail_x -= 1
					if(head_y < tail_y):
						tail_y -= 1
					elif(head_y > tail_y):
						tail_y += 1
				grid[tail_y][0] = grid[tail_y][0][:tail_x] + "#" + grid[tail_y][0][tail_x + 1:]

		case "R":
			for _ in range(int(amount)):
				head_x += 1
				if(CheckIfSkip(head_y, head_x, tail_y, tail_x)):
					continue

				if(head_x - tail_x > 1):
					tail_x += 1
					if(head_y < tail_y):
						tail_y -= 1
					elif(head_y > tail_y):
						tail_y += 1
				grid[tail_y][0] = grid[tail_y][0][:tail_x] + "#" + grid[tail_y][0][tail_x + 1:]

	i = input.readline()[:-1]

result = sum([row[0].count('#') for row in grid])

# part 1: 
print("day9: solution for part 1: " + str(result))

